import requests
from bs4 import BeautifulSoup
import pandas as pd
import time
import random
from fake_useragent import UserAgent

class PriceScraper:
    def __init__(self):
        self.ua = UserAgent()
        self.session = requests.Session()

    def get_headers(self):
        return {
            'User-Agent': self.ua.random,
            'Accept': 'text/html,application/xhtml+xml',
            'Accept-Language': 'en-US,en;q=0.9',
        }

    def scrape_competitor(self, url, price_selector):
        """Scrape price from competitor page."""
        try:
            resp = self.session.get(url, headers=self.get_headers())
            soup = BeautifulSoup(resp.content, 'html.parser')
            price_elem = soup.select_one(price_selector)
            if price_elem:
                price = float(price_elem.text.replace('$', '').replace(',', ''))
                return price
        except Exception as e:
            print(f"Error scraping {url}: {e}")
        return None

    def monitor_competitors(self, competitors, interval_hours=6):
        """Continuously monitor competitor prices."""
        results = []
        for name, config in competitors.items():
            price = self.scrape_competitor(config['url'], config['selector'])
            results.append({
                'competitor': name,
                'price': price,
                'timestamp': pd.Timestamp.now()
            })
            time.sleep(random.uniform(2, 5))  # Avoid detection
        return pd.DataFrame(results)

# Example usage
competitors = {
    'competitor_a': {
        'url': 'https://competitor-a.com/product',
        'selector': '.price-value'
    },
    'competitor_b': {
        'url': 'https://competitor-b.com/item',
        'selector': '#product-price'
    }
}

scraper = PriceScraper()
df = scraper.monitor_competitors(competitors)
df.to_csv('price_history.csv', mode='a')
